import{a as m}from"./chunk-4LJ3MSR5.js";import"./chunk-R6BOKDWR.js";import"./chunk-WFCKMT3O.js";import{a as c}from"./chunk-YRXP4OMK.js";import{a as l}from"./chunk-BVO2RBW3.js";import"./chunk-TYPJPCMS.js";import"./chunk-XUUUIT3D.js";import"./chunk-WMVF4ZO7.js";import{k as s}from"./chunk-6VCIUA7U.js";import{Ra as i,Za as n,sb as r,vb as o}from"./chunk-NCBRYYCQ.js";var b=class a{constructor(t,e){this.navigationService=t;this.breadcrumbService=e;t.setActiveLink("/experiments"),this.breadcrumbService.setBreadcrumbs([{label:"Landing",url:"/"},{label:"Experiments",url:"/experiments"},{label:"Motor Imagery",url:"/experiments/motor-imagery"},{label:"Unilateral Motor Imagery",url:"/experiments/motor-imagery/unilateral"}])}experiment={name:"Unilateral Motor Imagery",description:"This experiment focuses on the <b>mental rehearsal of movement</b> involving a <b>single limb</b>\u2014typically the left or right hand\u2014without any actual muscular activity. It is designed to evaluate a participant\u2019s ability to generate <b>consistent</b> and <b>classifiable brain signals</b> through motor imagery alone. <b>Unilateral motor imagery</b> is widely used in <b>brain-computer interface (BCI) research</b> due to its simplicity, <b>clear cortical activation patterns</b>, and effectiveness in training <b>decoding algorithms</b>.",metadata:{type:"Unilateral",paradigm:"Endogenous",stimulus_type:"Visual",recording_modality:"EEG",control_mode:"Offline",difficulty_level:"Moderate",class_range:{min:2,max:4},intended_applications:[{name:"Rehabilitation",url:"/applications/rehabilitation"},{name:"BCI Training",url:"/applications/bci-training"},{name:"Prosthetics",url:"/applications/prosthetics"}],common_datasets:[{name:"BCI Competition IV 2a",url:"/datasets/bci-competition-iv-2a"},{name:"PhysioNet EEG Motor Imagery",url:"/datasets/physionet-eeg-mi"}]},objective:"The main objective of this experiment is to assess the capability of individuals to voluntarily modulate their <b>sensorimotor rhythms (SMRs)</b> through the imagined movement of a <b>single limb</b>, typically the <b>left or right hand</b>. By focusing on <b>unilateral motor imagery</b>, the protocol aims to isolate specific <b>cortical activation patterns</b> that can be detected using <b>non-invasive brain recording techniques</b> such as <b>EEG</b>. These patterns are then used to evaluate the feasibility of <b>decoding motor intentions</b> for <b>brain-computer interface (BCI)</b> applications.",hypothesis:"We hypothesize that <b>unilateral motor imagery</b>\u2014specifically the imagined movement of a <b>single hand</b>\u2014produces <b>distinguishable</b> and <b>repeatable changes</b> in the <b>mu and beta frequency bands</b> over <b>sensorimotor cortex</b>. These changes can be reliably detected through <b>EEG</b> and used to <b>classify mental states</b> corresponding to <b>left or right hand motor imagery</b>, supporting the development of <b>accurate and robust BCI control strategies</b>.",procedure:"Participants are seated comfortably in a <b>controlled environment</b> and fitted with <b>EEG equipment</b>. Each trial begins with a <b>fixation cross</b> displayed at the center of the screen, followed by a <b>visual cue</b> (such as an arrow) indicating which hand the participant should imagine moving. The participant is instructed to <b>imagine performing the movement</b> (e.g., grasping or rotating the hand) <b>without any actual muscle contraction</b>. Each imagery period typically lasts between <b>4 to 6 seconds</b> and is followed by a <b>rest interval</b>. The protocol is repeated across <b>multiple trials</b> to ensure sufficient data for analysis. The entire session lasts approximately <b>20 to 40 minutes</b> depending on the number of classes and blocks.",requirements:"This experiment requires access to a <b>modern web browser</b> capable of rendering visual stimuli with <b>precise timing</b> (e.g., Chrome or Firefox). For experimental use with EEG systems, the platform must support <b>synchronized stimulus presentation</b> and integration with <b>external acquisition hardware</b> via <b>serial, WebUSB</b>, or <b>network-based protocols</b>. Participants should be instructed on <b>motor imagery techniques</b> and perform the task in a <b>quiet, distraction-free environment</b>. Optional features include <b>online feedback interfaces</b> and <b>real-time monitoring tools</b> for data quality assessment.",characteristics:"This experiment is classified as <b>endogenous</b> because it relies entirely on the participant\u2019s <b>internal cognitive processes</b>\u2014specifically <b>voluntary mental imagery</b>\u2014to generate the desired neural activity. It is considered <b>unilateral</b> because only one limb (either the left or the right hand) is imagined per trial, allowing for <b>clearer separation</b> of cortical activation and <b>simplified classification</b> of motor intentions within brain-computer interface systems."};static \u0275fac=function(e){return new(e||a)(i(l),i(c))};static \u0275cmp=n({type:a,selectors:[["app-unilateral"]],decls:1,vars:1,consts:[[3,"experiment"]],template:function(e,p){e&1&&o(0,"app-experiment",0),e&2&&r("experiment",p.experiment)},dependencies:[s,m],styles:[".unilateral-motor-imagery[_ngcontent-%COMP%]{background-color:var(--mat-sys-surface-container);padding:50px;text-align:center;display:flex;flex-direction:column;justify-content:center;align-items:center;height:400px}.hand-container[_ngcontent-%COMP%]{display:flex;flex-direction:column;align-items:center;gap:30px}.side-selector[_ngcontent-%COMP%]{display:flex;gap:20px;margin-bottom:20px}.side-btn[_ngcontent-%COMP%]{padding:10px 20px;border:2px solid var(--mat-sys-primary);background-color:transparent;color:var(--mat-sys-primary);border-radius:8px;cursor:pointer;transition:all .3s ease;font-size:14px;font-weight:500}.side-btn[_ngcontent-%COMP%]:hover{background-color:var(--mat-sys-primary-container)}.side-btn.selected[_ngcontent-%COMP%]{background-color:var(--mat-sys-primary);color:var(--mat-sys-on-primary)}.hand-display[_ngcontent-%COMP%]{display:flex;justify-content:center;align-items:center;min-height:200px}.hand[_ngcontent-%COMP%]{width:120px;text-align:center;transition:all .3s ease;opacity:.4;transform:scale(.8);transform-origin:center center}.hand.active[_ngcontent-%COMP%]{opacity:1;transform:scale(3)}.arrow-icon[_ngcontent-%COMP%]{font-size:60px;overflow:visible;margin-bottom:20px;color:var(--mat-sys-primary);display:block}.hand-label[_ngcontent-%COMP%]{font:var(--mat-sys-body-large);transition:all .3s ease;color:var(--mat-sys-on-surface);margin-top:10px}.center-indicator[_ngcontent-%COMP%]{margin-top:20px;opacity:.6}.center-indicator[_ngcontent-%COMP%]   mat-icon[_ngcontent-%COMP%]{font-size:24px;color:var(--mat-sys-outline)}.unilateral-hand[_ngcontent-%COMP%]{display:flex;flex-direction:column;align-items:center;justify-content:center}"]})};export{b as Unilateral};
