import{a as b}from"./chunk-EO5Z4LAL.js";import"./chunk-3DCJFLDG.js";import"./chunk-ZB3ZHSZP.js";import{a as m,b as c}from"./chunk-5MOYTFVM.js";import"./chunk-6AWGWZ7C.js";import"./chunk-VEDDZ7KK.js";import{c as s}from"./chunk-P3ALKQJN.js";import"./chunk-LQFSQHAU.js";import"./chunk-3MCZF45O.js";import"./chunk-DTS6JUDK.js";import"./chunk-B2AUY64Q.js";import{Ab as a,Db as o,Xa as i,db as n}from"./chunk-TEXVEQLC.js";import"./chunk-5KHZBZE7.js";var l=class r{constructor(t,e){this.navigationService=t;this.breadcrumbService=e;t.setActiveLink("/experiments/motor-imagery"),this.breadcrumbService.setBreadcrumbs([{label:"Home",url:"/home"},{label:"Experiments",url:"/experiments"},{label:"Motor Imagery",url:"/experiments/motor-imagery"}])}escapeHtml(t){let e=document.createElement("p");return e.innerText=t,e.innerHTML}motor_imagery={section:"Motor Imagery",link:"/experiments/motor-imagery",context:"Motor imagery (MI) refers to the <b>mental simulation of voluntary movement</b> in the absence of physical execution. It is a foundational paradigm in <b>brain-computer interface (BCI) systems</b>, enabling the decoding of <b>sensorimotor rhythms</b> linked to motor intent.",definition:"Motor imagery is a <b>cognitive process</b> involving the internal rehearsal of <b>goal-directed movements</b> without generating muscular output. It activates <b>sensorimotor brain regions</b> and induces measurable changes in <b>EEG signals</b>, particularly within the <b>mu (8\u201312 Hz)</b> and <b>beta (13\u201330 Hz)</b> frequency bands.",application:"Motor imagery is applied in <b>non-invasive BCI frameworks</b> across areas such as <b>neurorehabilitation</b>, <b>prosthetic control</b>, <b>augmentative communication</b>, and <b>cognitive training</b>. Its reliance on <b>endogenously generated brain activity</b> makes it suitable for both <b>clinical implementation</b> and <b>experimental research</b>.",description:"Motor imagery (MI) enables the modulation of <b>sensorimotor cortical activity</b> through imagined movement, without muscular engagement. It is characterized by <b>event-related desynchronization (ERD)</b> and <b>event-related synchronization (ERS)</b> in the <b>sensorimotor cortex</b>, observable in EEG recordings. These neural patterns form the foundation for <b>intent decoding</b> in BCI applications, supporting interaction with external systems via <b>mental command</b>.",explore:"Explore Motor Imagery Experiments",explore_text:"The following experiments represent distinct implementations of the motor imagery paradigm, each varying in how <b>stimuli are presented and structured</b> across trials. These designs\u2014such as <b>unilateral</b>, <b>bilateral</b>, <b>word-cued</b>, or <b>sequential</b>\u2014are intended to evoke brain responses that can be classified reliably using non-invasive signals. Select an experiment to view its structure, methodology, and relevance in the context of BCI development.",mechanism:"Motor imagery engages cortical and subcortical circuits typically involved in actual movement execution, including the <b>primary motor cortex (M1)</b>, <b>supplementary motor area (SMA)</b>, <b>premotor cortex</b>, and <b>parietal regions</b>. Although no overt movement occurs, MI elicits activity patterns that closely resemble those produced during real motion, particularly within the <b>sensorimotor cortex</b>. These activations influence <b>oscillatory brain dynamics</b>\u2014notably <b>event-related desynchronization (ERD)</b> and <b>synchronization (ERS)</b>\u2014which can be detected through non-invasive methods such as <b>EEG</b> or <b>fNIRS</b>.",experiments:[{name:"Unilateral Motor Imagery",link:"/experiments/motor-imagery/unilateral",tier:1,description:"This experiment focuses on the <b>imagination of movement</b> in a <b>single limb</b>, typically the left or right hand. By isolating <b>unilateral activity</b>, researchers can analyze <b>contralateral desynchronization</b> in sensorimotor cortex and assess the feasibility of decoding <b>distinct motor intentions</b> from EEG signals."},{name:"Bilateral Motor Imagery",link:"/experiments/motor-imagery/bilateral",tier:2,description:"This variant involves the <b>simultaneous</b> or <b>alternating imagination</b> of movements in <b>both hands</b>. It enables the study of <b>interhemispheric interactions</b> and <b>coordination patterns</b> in sensorimotor EEG activity."},{name:"Intentional Motor Imagery",link:"/experiments/motor-imagery/intentional",tier:2,description:"This paradigm separates the <b>instruction phase</b> from the <b>execution cue</b>. Participants are first informed about the upcoming imagery task (e.g., \u201CLeft Hand\u201D) and must <b>withhold execution</b> until a subsequent \u201CGo\u201D signal appears. This structure enables the study of <b>voluntary motor initiation</b>, <b>preparatory neural activity</b>, and <b>intentional control</b> in BCI contexts."},{name:"Blocked Motor Imagery",link:"/experiments/motor-imagery/blocked",tier:2,description:"In this design, <b>motor imagery</b> and <b>resting trials</b> are grouped into <b>sequential blocks</b> instead of being randomized. This structure facilitates the analysis of <b>sustained cortical engagement</b> over time."},{name:"Word-Cued Motor Imagery",link:"/experiments/motor-imagery/wordcued",tier:2,description:"Instead of arrows or icons, this experiment uses <b>textual stimuli</b> (e.g., \u201CLeft\u201D, \u201CRight\u201D, \u201CFeet\u201D) to instruct motor imagery. It provides a <b>linguistic interface</b> for participants and enables analysis of <b>language-motor integration</b> effects."},{name:"Sequential Motor Imagery",link:"/experiments/motor-imagery/sequential",tier:2,description:"Participants receive <b>temporally structured cues</b> guiding a <b>sequence of imagined actions</b> (e.g., grasp \u2192 rotate). This variant targets the decoding of <b>multi-phase motor plans</b> and explores <b>temporal segmentation</b> in EEG."}]};static \u0275fac=function(e){return new(e||r)(i(m),i(c))};static \u0275cmp=n({type:r,selectors:[["app-motor-imagery"]],decls:1,vars:1,consts:[[3,"paradigm"]],template:function(e,d){e&1&&o(0,"app-paradigm",0),e&2&&a("paradigm",d.motor_imagery)},dependencies:[s,b],encapsulation:2})};export{l as MotorImagery};
